import "./comments.scss";
import { useContext } from "react";
import { AuthContext } from "../../context/authContext";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { makeRequest } from "../../axios";
import moment from "moment"

const Comments = (postId) => {
  const { currentUser } = useContext(AuthContext);
  const queryClient = useQueryClient();
  const [desc, setDesc] = useState("")

  const { isLoading, error, data } = useQuery({
    queryKey: ["comments"],
    queryFn: () => makeRequest.get("/comments?postId=", postId['postId']).then((res) => {
      console.log("/comments?postId=", res.data)
      return res.data;
    })
  }
  );
  console.log(data)

  const mutation = useMutation({
    mutationFn: (newComment) => {
      return makeRequest.post('/comments', newComment)
    },
    onSuccess: () => {
      // Invalidate and refetch(refresh)
      queryClient.invalidateQueries({ queryKey: ['comments'] })
    },
  })

  const handleClick = async (e, res) => {
    e.preventDefault()

    try {
      mutation.mutate({ desc: desc, postId: postId })
      setDesc("")

    } catch (error) {
      res.status(500).json("Error in Fetching the uploaded data : " + error)
    }
  }

  return (
    <div className="comments">
      <div className="write">
        <img src={currentUser.profilePic} alt="" />
        <input type="text" placeholder="write a comment" />
        <button>Send</button>
      </div>
      {error ? "Something went wrong in loading comments!"
        : (isLoading ? "Loading comments..." : data.map((comment) => (
          <div className="comment">
            <img src={comment.profilePicture} alt="" />
            <div className="info">
              <span>{comment.name}</span>
              <p>{comment.desc}</p>
            </div>
            <span className="date">{moment(comment.createdAt).fromNow()}</span>
          </div>
        )))}
    </div>
  );
};

export default Comments;
