import "./share.scss";
import Image from "../../assets/img.png";
import Map from "../../assets/map.png";
import Friend from "../../assets/friend.png";
import { useContext } from "react";
import { AuthContext } from "../../context/authContext";
import { useState } from "react";
import { useMutation, useQueryClient } from '@tanstack/react-query'
import { makeRequest } from "../../axios";

const Share = () => {

  const queryClient = useQueryClient();
  const [file, setFile] = useState(null)
  const [desc, setDesc] = useState("")

  const upload = async () => {
    try {
      const { currentUser } = useContext(AuthContext)
      const formData = new FormData();
      formData.append("file", file);
      const res = await makeRequest.post("/upload", formData)
      return res.data;
    } catch (error) {
      console.log(error)
    }
  }

  const mutation = useMutation({
    mutationFn: (newPost) => {
      return makeRequest.post('/posts', newPost)
    },
    onSuccess: () => {
      // Invalidate and refetch(refresh)
      queryClient.invalidateQueries({ queryKey: ['posts'] })
    },
  })

  const handleClick = async (e, res) => {
    e.preventDefault()

    try {
      let imgUrl = ""
      if (file) imgUrl = await upload();
      mutation.mutate({ desc: desc, image: imgUrl })

      setDesc("")
      setFile(null)

    } catch (error) {
      res.status(500).json("Error in Fetching the uploaded data : " + error)
    }
  }

  return (
    <div className="share">
      <div className="container">
        <div className="top">
          <div className="left">
            <img src={"./upload/" + currentUser.profilePic} alt="" />
            <input type="text" placeholder={`What's on your mind ${currentUser.name}?`}
              onChange={e => setDesc(e.target.value)} value={desc} />
          </div>
          <div className="right">
            {file && <img className="file" src={URL.createObjectURL(file)} alt="img u selected" ur></img>}
          </div>
        </div>
        <hr />
        <div className="bottom">
          <div className="left">
            <input type="file" id="file" style={{ display: "none" }}
              onChange={e => { setFile(e.target.files[0]) }} />
            <label htmlFor="file">
              <div className="item">
                <img src={Image} alt="" />
                <span>Add Image</span>
              </div>
            </label>
            <div className="item">
              <img src={Map} alt="" />
              <span>Add Place</span>
            </div>
            <div className="item">
              <img src={Friend} alt="" />
              <span>Tag Friends</span>
            </div>
          </div>
          <div className="right">
            <button onClick={handleClick}>Share</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Share;
